#include <iostream>#include <fstream>#include <cstring>#include <cstdlib>using namespace std;fstream fpsdc;bool readsdc(){  string line;  while(1) { //    cout<<"line = "<<line<<endl;    getline(fpsdc,line);      if(line.find("set_max_delay")!=-1) break;       }  if(fpsdc.eof()) {cout<<"can't find delay"<<endl;return false;} // cout<<"line= "<<line<<endl;  int pos1 = line.find("set_max_delay");  int pos2 = pos1+13; // set_max_delay«á¤@®æ    string subline = line.substr(pos2+1); // cout<<"subline = "<<subline<<endl;  int pos3 = subline.find_first_of(" ");  string sdc_time_constraint = subline.substr(0,pos3); // cout<<"maxdelay="<<sdc_time_constraint<<endl;    int timeconstraint = atoi(sdc_time_constraint.c_str());   required_time = timeconstraint;   return true;}